    private void btnAdminActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAdminActionPerformed
     
        login_Admin admin = new login_Admin();
        
        if (seleccion == 1) {
            
            admin.jLabeltitulo.setText("LOGIN ADMIN");
            admin.jLabelUsuario.setText("User");
            admin.jLabelcontrasena.setText("Password");
            admin.btnIniciar.setText("Log In");
            admin.jButtonCREARADMIN.setText("Create Admin");
            
        } else if (seleccion == 0) {
            
            admin.jLabeltitulo.setText("LOGIN ADMIN");
            admin.jLabelUsuario.setText("Usuario");
            admin.jLabelcontrasena.setText("Contrase単a");
            admin.btnIniciar.setText("Iniciar");
            admin.jButtonCREARADMIN.setText("Crear Admin");
        }
        
        admin.seleccion = seleccion;
        admin.setVisible(true);
        this.setVisible(false);
        
    }//GEN-LAST:event_btnAdminActionPerformed

