    private void cbxIdiomaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbxIdiomaActionPerformed
          
   seleccion = cbxIdioma.getSelectedIndex();
      
   if  (seleccion == 1) {
       
      this.titulologin.setText("HOW DO YOU WANT TO LOGIN IN ?");
      this.salir.setText("Exit");
      this.btnAdmin.setText("Admin");
      this.btnEmpleado.setText("Employee");
      
   } else if (seleccion == 0){
       
      this.titulologin.setText("COMO DESEA INICIAR SESION ?");
      this.salir.setText("Salir");
      this.btnAdmin.setText("Admin");
      this.btnEmpleado.setText("Empleado");
   }
           
    }//GEN-LAST:event_cbxIdiomaActionPerformed

